import { MapboxAutofill } from '@mapbox/search-js-core';
import { renderHook, act } from '@testing-library/react-hooks';

import { useMapboxAutofill } from '../../src/hooks/useMapboxAutofill';

const ACCESS_TOKEN = 'xyz';

describe('useMapboxAutofill', () => {
  it('works with access token', () => {
    const { result, rerender } = renderHook(
      ({ accessToken }) => useMapboxAutofill({ accessToken }),
      { initialProps: { accessToken: ACCESS_TOKEN } }
    );

    expect(result.current).toEqual(expect.any(MapboxAutofill));
    expect(result.current.accessToken).toEqual(ACCESS_TOKEN);

    act(() => {
      rerender({ accessToken: 'abc' });
    });

    expect(result.current.accessToken).toEqual('abc');
  });

  it('works with options', () => {
    const { result, rerender } = renderHook(
      ({ options }) =>
        useMapboxAutofill({ accessToken: ACCESS_TOKEN, ...options }),
      { initialProps: { options: {} } }
    );

    expect(result.current).toEqual(expect.any(MapboxAutofill));
    expect(result.current.defaults).toEqual(MapboxAutofill.defaults);

    act(() => {
      rerender({ options: { language: 'jp' } });
    });

    expect(result.current.defaults).toEqual({
      ...MapboxAutofill.defaults,
      language: 'jp'
    });

    // Make sure previous language is "undone".
    act(() => {
      rerender({ options: { proximity: [0, 0] } });
    });

    expect(result.current.defaults).toEqual({
      ...MapboxAutofill.defaults,
      language: 'en',
      proximity: [0, 0]
    });
  });
});
